import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const PasienList = () => {
  const [pasien, setPasien] = useState([]);

  useEffect(() => {
    getPasien();
  }, []);

  const getPasien = async () => {
    try {
      const response = await axios.get("http://localhost:5000/pasien");
      setPasien(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const deletePasien = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/pasien/${id}`);
      getPasien();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-full">
        <Link to={"add"} className="button is-success mb-3">
          Tambah Pasien
        </Link>
        <table className="table is-striped is-fullwidth">
          <thead>
            <tr>
              <th>No</th>
              <th>Nama</th>
              <th>Tanggal Lahir</th>
              <th>Jenis Kelamin</th>
              <th>No. Telepon</th>
              <th>Alamat</th>
              <th>Nama Dokter</th>
              <th style={{ width: "180px" }}>Aksi</th>
            </tr>
          </thead>
          <tbody>
            {pasien.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.nama}</td>
                <td>{item.tgl_lahir}</td>
                <td>{item.gender}</td>
                <td>{item.no_telp}</td>
                <td>{item.alamat}</td>
                <td>{item.dokter}</td>
                <td>
                  <div className="buttons">
                    <Link
                      to={`edit/${item.id}`}
                      className="button is-small is-info"
                    >
                      Edit
                    </Link>
                    <button
                      onClick={() => deletePasien(item.id)}
                      className="button is-small is-danger"
                    >
                      Hapus
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default PasienList;
