import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const StrukPemeriksaan = () => {
  const { id } = useParams(); // id struk dari URL
  const [struk, setStruk] = useState({}); // state untuk data struk

  useEffect(() => {
    fetchStruk();
  }, []);

  const fetchStruk = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/periksa/${id}`);
      setStruk(res.data);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <div className="box p-5">
          <h1 className="title has-text-centered">Struk Pemeriksaan</h1>
          <p><strong>Nama Pasien:</strong> {struk.pasien?.nama}</p>
          <p><strong>Tanggal Periksa:</strong> {struk.tanggal_periksa}</p>
          <p><strong>Biaya Periksa:</strong> {struk.biaya_periksa}</p>
          <p><strong>Obat:</strong> {struk.obat?.nama_obat}</p>
          <p><strong>Harga Obat:</strong> {struk.obat?.harga}</p>
          <p><strong>Total Biaya:</strong> {Number(struk.periksa?.biaya_periksa) + Number(struk.obat?.harga)}</p>
        </div>
      </div>
    </div>
  );
};

export default StrukPemeriksaan;
