import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const Struk = ({ id }) => {
  const [struk, setStruk] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/struk/${id_struk}`);
        console.log(response.data);  // Log data respons untuk memastikan isinya
        setStruk(response.data);
        setLoading(false);  // Menandakan bahwa data sudah berhasil diambil
      } catch (error) {
        console.error("Error fetching struk data:", error);
        setLoading(false);  // Tetap ubah loading menjadi false meskipun ada error
      }
    };
    fetchData();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!struk) {
    return <div>No data found</div>;
  }

  // Akses data dengan memastikan ada data terkait pasien
  const pasienNama = struk.Pasien?.nama || 'Nama Pasien Tidak Ditemukan';

  return (
    <div>
      <h1>Struk Detail</h1>
      <p>Nama Pasien: {pasienNama}</p>
      {/* Tampilkan data lainnya */}
    </div>
  );
};


export default Struk;
