import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const Struk = () => {
  const { id_struk } = useParams();
  const [struk, setStruk] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStruk();
  }, []);

  const fetchStruk = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/pasien/periksa/struk/${id_struk}`);
      setStruk(res.data);
    } catch (error) {
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="columns mt-5 is-centered">
        <div className="column is-half has-text-centered">
          <p>Loading struk...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        {struk ? (
          <div className="box p-5">
            <h1 className="title has-text-centered">Struk Pemeriksaan</h1>
            <div className="content">
              <p><strong>Nama Pasien:</strong> {struk.Pasien?.nama || "Tidak tersedia"}</p>
              <p><strong>Tanggal Periksa:</strong> {struk.Periksa?.tanggal_periksa || "Tidak tersedia"}</p>
              <p><strong>Biaya Periksa:</strong> Rp{struk.Periksa?.biaya_periksa?.toLocaleString() || "0"}</p>
              <p><strong>Obat:</strong> {struk.Obat?.nama_obat || "Tidak tersedia"}</p>
              <p><strong>Harga Obat:</strong> Rp{struk.Obat?.harga?.toLocaleString() || "0"}</p>
              <p><strong>Total Biaya:</strong> Rp{struk.total_biaya?.toLocaleString() || "0"}</p>
            </div>
          </div>
        ) : (
          <div className="notification is-danger has-text-centered">
            Data struk tidak ditemukan.
          </div>
        )}
      </div>
    </div>
  );
};

export default Struk;
